import React from "react";
import "../styles/Header.scss";
import "../styles/ActionMenu.scss";
import Button from "@material-ui/core/Button";
import { Link } from "react-router-dom";

export default function ActionMenu(props) {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleNo = () => {
    setOpen(false);
  };

  const handleYes = () => {
    setOpen(false);
    logout();
  };

  const logout = () => {
    // TODO: uitloggen zorgt niet dat je terug gaat naar login
    let params = new URLSearchParams(window.location.search);
    console.log(params);

    //als wel key
    if (params.get("key")) {
      window.location.href = window.location.href.split("?")[0];
      window.localStorage.clear();
    } else {
      //als geen key
      window.localStorage.clear();
      window.location.reload();
    }
  };

  return (
    <div className="HeaderWrapper">
      <h2 style={{ padding: "50px 5px 0px" }}>Maak uw keuze:</h2>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          margin: "5px 5px 5px 5px",
        }}
      >
        <Link to={"/scanner"} style={{ textDecoration: "none" }}>
          <Button
            color="primary"
            variant="contained"
            style={{
              minWidth: "300px",
              minHeight: "100px",
              margin: "30px 10px 10px",
            }}
          >
            {"Inslag <add icon>"}
          </Button>
        </Link>
        <Link to={"/scanner"} style={{ textDecoration: "none" }}>
          <Button
            color="primary"
            variant="contained"
            style={{
              minWidth: "300px",
              minHeight: "100px",
              margin: "30px 10px 10px",
            }}
          >
            {"Uitslag <add icon>"}
          </Button>
        </Link>
      </div>

      <center>
        <List component="nav" aria-labelledby="nested-list-subheader">
          <ListItem button onClick={handleClickOpen}>
            <ListItemText primary="Taak 1" />
          </ListItem>
          <ListItem button onClick={handleClickOpen}>
            <ListItemText primary="Taak 2" />
          </ListItem>
          <ListItem button onClick={handleClickOpen}>
            <ListItemText primary="Taak 3" />
          </ListItem>
        </List>
        <Divider />
        <List component="nav" aria-labelledby="nested-list-subheader">
          <ListItem button onClick={handleClickOpen}>
            <ListItemIcon>
              <ExitIcon />
            </ListItemIcon>
            <ListItemText primary="Uitloggen" />
          </ListItem>
        </List>
        <Dialog
          open={open}
          onClose={handleNo} // makes it possible to click outside the dialog window to close it
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"
        >
          <DialogTitle id="alert-dialog-title">
            {"Weet u zeker dat u wilt uitloggen?"}
          </DialogTitle>
          {/* <DialogContent>
          <DialogContentText id="alert-dialog-description">
            Let Google help apps determine location. This means sending
            anonymous location data to Google, even when no apps are running.
          </DialogContentText>
        </DialogContent> */}
          <DialogActions>
            <Button onClick={handleNo} color="primary">
              Nee
            </Button>
            <Button onClick={handleYes} color="primary" autoFocus>
              Ja
            </Button>
          </DialogActions>
        </Dialog>
      </center>
    </div>
  );
}
